import React, { useState, useEffect } from "react";
import * as XLSX from "xlsx";

const getCurrentDate = () => new Date().toISOString().split("T")[0];

const App = () => {
  const [loggedIn, setLoggedIn] = useState(false);
  const [password, setPassword] = useState("");
  const [tasks, setTasks] = useState(() => {
    const saved = localStorage.getItem("tasks");
    return saved ? JSON.parse(saved) : {};
  });

  const [comment, setComment] = useState("");
  const [date, setDate] = useState(getCurrentDate());

  useEffect(() => {
    localStorage.setItem("tasks", JSON.stringify(tasks));
  }, [tasks]);

  const handleLogin = () => {
    if (password === "admin123") {
      setLoggedIn(true);
    } else {
      alert("Wrong password!");
    }
  };

  const handleCount = (delta) => {
    setTasks((prev) => {
      const current = prev[date] || { count: 0, completed: false, comments: "" };
      return {
        ...prev,
        [date]: { ...current, count: Math.max(0, current.count + delta) },
      };
    });
  };

  const handleComment = () => {
    setTasks((prev) => {
      const current = prev[date] || { count: 0, completed: false, comments: "" };
      return {
        ...prev,
        [date]: { ...current, comments: comment },
      };
    });
    setComment("");
  };

  const toggleComplete = () => {
    setTasks((prev) => {
      const current = prev[date] || { count: 0, completed: false, comments: "" };
      return {
        ...prev,
        [date]: { ...current, completed: !current.completed },
      };
    });
  };

  const exportExcel = (filter) => {
    const rows = [["Date", "Count", "Completed", "Comments"]];
    for (const [key, val] of Object.entries(tasks)) {
      const d = new Date(key);
      const now = new Date(date);
      if (
        (filter === "week" && getWeek(d) === getWeek(now)) ||
        (filter === "month" && d.getMonth() === now.getMonth()) ||
        (filter === "year" && d.getFullYear() === now.getFullYear())
      ) {
        rows.push([key, val.count, val.completed ? "Yes" : "No", val.comments]);
      }
    }
    const wb = XLSX.utils.book_new();
    const ws = XLSX.utils.aoa_to_sheet(rows);
    XLSX.utils.book_append_sheet(wb, ws, "Tasks");
    XLSX.writeFile(wb, `tasks-${filter}.xlsx`);
  };

  const getWeek = (date) => {
    const onejan = new Date(date.getFullYear(), 0, 1);
    return Math.ceil((((date - onejan) / 86400000) + onejan.getDay() + 1) / 7);
  };

  if (!loggedIn) {
    return (
      <div className="p-10 text-center">
        <h1 className="text-2xl mb-4">Task Manager Login</h1>
        <input
          type="password"
          className="border p-2"
          placeholder="Enter password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <button
          className="bg-blue-500 text-white px-4 py-2 ml-2"
          onClick={handleLogin}
        >
          Login
        </button>
      </div>
    );
  }

  const current = tasks[date] || { count: 0, completed: false, comments: "" };

  return (
    <div className="p-6 max-w-xl mx-auto">
      <h1 className="text-2xl font-bold mb-4">Task Manager</h1>
      <input
        type="date"
        value={date}
        onChange={(e) => setDate(e.target.value)}
        className="border p-2 mb-4 w-full"
      />
      <div className="flex justify-between items-center mb-4">
        <span className="text-lg">Tickets: {current.count}</span>
        <div>
          <button onClick={() => handleCount(1)} className="px-3 py-1 bg-green-500 text-white mr-2">+</button>
          <button onClick={() => handleCount(-1)} className="px-3 py-1 bg-red-500 text-white">-</button>
        </div>
      </div>
      <div className="mb-4">
        <input
          type="text"
          value={comment}
          placeholder="Add comment"
          onChange={(e) => setComment(e.target.value)}
          className="border p-2 w-full"
        />
        <button onClick={handleComment} className="mt-2 px-4 py-2 bg-blue-500 text-white">Save Comment</button>
      </div>
      <div className="mb-4">
        <label className="mr-2">Completed:</label>
        <input type="checkbox" checked={current.completed} onChange={toggleComplete} />
      </div>
      <div className="flex gap-2">
        <button onClick={() => exportExcel("week")} className="bg-yellow-400 px-4 py-2 text-white">Export Week</button>
        <button onClick={() => exportExcel("month")} className="bg-yellow-500 px-4 py-2 text-white">Export Month</button>
        <button onClick={() => exportExcel("year")} className="bg-yellow-600 px-4 py-2 text-white">Export Year</button>
      </div>
    </div>
  );
};

export default App;
