import React, { useState, useEffect } from "react";
import * as XLSX from "xlsx";

const getCurrentDate = () => new Date().toISOString().split("T")[0];

const App = () => {
  const [loggedIn, setLoggedIn] = useState(false);
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [user, setUser] = useState("");
  const [users, setUsers] = useState(() => {
    const saved = localStorage.getItem("users");
    return saved ? JSON.parse(saved) : {};
  });
  const [tasks, setTasks] = useState({});
  const [comment, setComment] = useState("");
  const [date, setDate] = useState(getCurrentDate());

  useEffect(() => {
    if (user) {
      const userTasks = localStorage.getItem(`tasks_${user}`);
      setTasks(userTasks ? JSON.parse(userTasks) : {});
    }
  }, [user]);

  useEffect(() => {
    if (user) {
      localStorage.setItem(`tasks_${user}`, JSON.stringify(tasks));
    }
  }, [tasks, user]);

  useEffect(() => {
    localStorage.setItem("users", JSON.stringify(users));
  }, [users]);

  const handleLogin = () => {
    if (users[username] && users[username] === password) {
      setLoggedIn(true);
      setUser(username);
    } else {
      alert("Invalid username or password!");
    }
  };

  const handleRegister = () => {
    if (username in users) {
      alert("User already exists!");
    } else {
      setUsers((prev) => ({ ...prev, [username]: password }));
      alert("User registered successfully! Now you can log in.");
    }
  };

  const handleCount = (delta) => {
    setTasks((prev) => {
      const current = prev[date] || { count: 0, completed: false, comments: [] };
      return {
        ...prev,
        [date]: { ...current, count: Math.max(0, current.count + delta) },
      };
    });
  };

  const handleComment = () => {
    if (!comment.trim()) return;
    setTasks((prev) => {
      const current = prev[date] || { count: 0, completed: false, comments: [] };
      const comments = Array.isArray(current.comments)
        ? current.comments
        : current.comments
        ? [current.comments]
        : [];
      return {
        ...prev,
        [date]: {
          ...current,
          comments: [...comments, comment.trim()],
        },
      };
    });
    setComment("");
  };

  const handleCommentKeyPress = (e) => {
    if (e.key === "Enter") {
      e.preventDefault();
      handleComment();
    }
  };

  const toggleComplete = () => {
    setTasks((prev) => {
      const current = prev[date] || { count: 0, completed: false, comments: [] };
      return {
        ...prev,
        [date]: { ...current, completed: !current.completed },
      };
    });
  };

  const exportExcel = (filter) => {
    const rows = [["Date", "Count", "Completed", "Comments"]];
    for (const [key, val] of Object.entries(tasks)) {
      const d = new Date(key);
      const now = new Date(date);
      if (
        (filter === "week" && getWeek(d) === getWeek(now)) ||
        (filter === "month" && d.getMonth() === now.getMonth()) ||
        (filter === "year" && d.getFullYear() === now.getFullYear())
      ) {
        const commentsArray = Array.isArray(val.comments)
          ? val.comments
          : val.comments
          ? [val.comments]
          : [];
        rows.push([
          key,
          val.count,
          val.completed ? "Yes" : "No",
          commentsArray.join("; "),
        ]);
      }
    }
    const wb = XLSX.utils.book_new();
    const ws = XLSX.utils.aoa_to_sheet(rows);
    XLSX.utils.book_append_sheet(wb, ws, "Tasks");
    XLSX.writeFile(wb, `tasks-${filter}-${user}.xlsx`);
  };

  const getWeek = (date) => {
    const onejan = new Date(date.getFullYear(), 0, 1);
    return Math.ceil((((date - onejan) / 86400000) + onejan.getDay() + 1) / 7);
  };

  const rawCurrent = tasks[date] || { count: 0, completed: false, comments: [] };
  const current = {
    ...rawCurrent,
    comments: Array.isArray(rawCurrent.comments)
      ? rawCurrent.comments
      : rawCurrent.comments
      ? [rawCurrent.comments]
      : [],
  };

  if (!loggedIn) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-[#ffecd2] to-[#fcb69f]">
        <div className="bg-white shadow-2xl rounded-xl p-10 text-center w-96 animate-fadeIn relative">
          <img src="https://img.icons8.com/clouds/100/task.png" alt="logo" className="mx-auto mb-4" />
          <h1 className="text-3xl font-bold mb-6 text-orange-700">Task Manager</h1>
          <input
            type="text"
            className="border p-2 rounded w-full mb-3"
            placeholder="Username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
          <input
            type="password"
            className="border p-2 rounded w-full mb-4"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
          <div className="flex justify-between">
            <button
              className="bg-green-500 hover:bg-green-600 text-white px-4 py-2 rounded shadow"
              onClick={handleRegister}
            >
              Register
            </button>
            <button
              className="bg-indigo-500 hover:bg-indigo-600 text-white px-4 py-2 rounded shadow"
              onClick={handleLogin}
            >
              Login
            </button>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-r from-[#dce35b] to-[#45b649] p-6">
      <div className="bg-white rounded-2xl shadow-xl p-8 max-w-2xl mx-auto animate-fadeIn">
        <div className="flex justify-between items-center mb-6">
          <div className="flex items-center gap-3">
            <img src="https://img.icons8.com/clouds/48/task.png" alt="logo" />
            <h1 className="text-2xl font-bold text-green-700">{user}'s Task Manager</h1>
          </div>
          <button onClick={() => window.location.reload()} className="text-sm bg-gray-300 hover:bg-gray-400 px-3 py-1 rounded">Logout</button>
        </div>
        <input
          type="date"
          value={date}
          onChange={(e) => setDate(e.target.value)}
          className="border p-2 rounded w-full mb-4 focus:ring-2 focus:ring-green-300"
        />
        <div className="flex justify-between items-center mb-4">
          <span className="text-lg font-semibold text-gray-800">🎯 Tickets: {current.count}</span>
          <div className="flex gap-2">
            <button onClick={() => handleCount(1)} className="bg-green-500 hover:bg-green-600 text-white px-3 py-1 rounded-full shadow">+</button>
            <button onClick={() => handleCount(-1)} className="bg-red-500 hover:bg-red-600 text-white px-3 py-1 rounded-full shadow">-</button>
          </div>
        </div>
        <div className="mb-4 flex items-center gap-2">
          <input
            type="text"
            value={comment}
            placeholder="💬 Add comment"
            onChange={(e) => setComment(e.target.value)}
            onKeyDown={handleCommentKeyPress}
            className="border p-2 rounded w-full focus:ring-2 focus:ring-green-300"
          />
          <button onClick={handleComment} className="bg-blue-500 hover:bg-blue-600 text-white px-3 py-2 rounded-full shadow">💾</button>
        </div>
        {current.comments.length > 0 && (
          <div className="mt-4 p-4 bg-gray-100 rounded-lg shadow-inner">
            <h2 className="font-semibold text-gray-700 mb-2">💬 Saved Comments</h2>
            <ul className="list-disc list-inside space-y-1 text-gray-800">
              {current.comments.map((c, i) => (
                <li key={i}>{c}</li>
              ))}
            </ul>
          </div>
        )}
        <div className="mb-4 flex items-center gap-3 mt-4">
          <label className="text-gray-700 font-medium">✅ Completed:</label>
          <input type="checkbox" checked={current.completed} onChange={toggleComplete} className="w-5 h-5" />
        </div>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-3 mt-6">
          <button onClick={() => exportExcel("week")} className="bg-yellow-400 hover:bg-yellow-500 text-white py-2 rounded shadow">Export Week</button>
          <button onClick={() => exportExcel("month")} className="bg-yellow-500 hover:bg-yellow-600 text-white py-2 rounded shadow">Export Month</button>
          <button onClick={() => exportExcel("year")} className="bg-yellow-600 hover:bg-yellow-700 text-white py-2 rounded shadow">Export Year</button>
        </div>
      </div>
    </div>
  );
};

export default App;
