import React, { useState, useEffect } from "react";
import * as XLSX from "xlsx";

const getCurrentDate = () => new Date().toISOString().split("T")[0];
const getCurrentTime = () =>
  new Date().toLocaleTimeString([], { hour: "2-digit", minute: "2-digit", hour12: true });

const LOGO_URL = "https://cdn-icons-png.flaticon.com/512/1055/1055687.png";
const BACKGROUND_IMAGE = process.env.PUBLIC_URL + "/black-stars-bg.png";
const CLOCK_ICON_URL = "https://cdn-icons-png.flaticon.com/512/595/595016.png";

const App = () => {
  const [loggedIn, setLoggedIn] = useState(() => !!localStorage.getItem("activeUser"));
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [user, setUser] = useState(() => localStorage.getItem("activeUser") || "");
  const [users, setUsers] = useState(() => {
    const saved = localStorage.getItem("users");
    return saved ? JSON.parse(saved) : {};
  });
  const [tasks, setTasks] = useState({});
  const [comment, setComment] = useState("");
  const [date, setDate] = useState(getCurrentDate());
  const [exportRange, setExportRange] = useState("week");

  useEffect(() => {
    if (user) {
      const userTasks = localStorage.getItem(`tasks_${user}`);
      setTasks(userTasks ? JSON.parse(userTasks) : {});
    }
  }, [user]);

  useEffect(() => {
    if (user) {
      localStorage.setItem(`tasks_${user}`, JSON.stringify(tasks));
    }
  }, [tasks, user]);

  useEffect(() => {
    localStorage.setItem("users", JSON.stringify(users));
  }, [users]);

  const handleLogin = () => {
    if (users[username] === password) {
      localStorage.setItem("activeUser", username);
      setUser(username);
      setLoggedIn(true);
    } else {
      alert("Invalid credentials");
    }
  };

  const handleRegister = () => {
    if (!users[username]) {
      setUsers(prev => ({ ...prev, [username]: password }));
      alert("User registered. You can now login.");
    } else {
      alert("User already exists");
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("activeUser");
    setLoggedIn(false);
    setUser("");
    setTasks({});
  };

  const changeDate = (delta) => {
    const d = new Date(date);
    d.setDate(d.getDate() + delta);
    setDate(d.toISOString().split("T")[0]);
  };

  const addComment = () => {
    if (!comment.trim()) return;
    const entry = { text: comment.trim(), time: getCurrentTime() };
    setTasks(prev => ({
      ...prev,
      [date]: {
        ...prev[date],
        comments: [...(prev[date]?.comments || []), entry],
        count: prev[date]?.count || 0
      }
    }));
    setComment("");
  };

  const exportToExcel = () => {
    const data = [];
    const currentDate = new Date(date);
    const range = exportRange === "week" ? 7 : exportRange === "month" ? 30 : 365;
    for (let i = 0; i < range; i++) {
      const d = new Date(currentDate);
      d.setDate(d.getDate() - i);
      const dStr = d.toISOString().split("T")[0];
      const entry = tasks[dStr];
      if (entry) {
        entry.comments?.forEach(c => {
          data.push({ Date: dStr, Comment: c.text, Time: c.time });
        });
      }
    }
    const ws = XLSX.utils.json_to_sheet(data);
    const wb = XLSX.utils.book_new();
    XLSX.utils.book_append_sheet(wb, ws, "Tasks");
    XLSX.writeFile(wb, `tasks_${exportRange}.xlsx`);
  };

  const current = tasks[date] || { count: 0, comments: [] };

  if (!loggedIn) {
    return (
      <div className="min-h-screen flex flex-col items-center justify-center" style={{
        backgroundImage: `url('${BACKGROUND_IMAGE}')`, backgroundRepeat: 'repeat', backgroundSize: 'auto'
      }}>
        <img src={LOGO_URL} alt="Logo" className="w-16 mb-4" />
        <h1 className="text-xl font-bold mb-4">Task Manager</h1>
        <input value={username} onChange={e => setUsername(e.target.value)} placeholder="Username"
               className="mb-2 p-2 border rounded" />
        <input type="password" value={password} onChange={e => setPassword(e.target.value)} placeholder="Password"
               className="mb-2 p-2 border rounded" />
        <div className="space-x-2">
          <button onClick={handleLogin} className="bg-blue-500 text-white px-4 py-1 rounded">Login</button>
          <button onClick={handleRegister} className="bg-green-500 text-white px-4 py-1 rounded">Register</button>
        </div>
      </div>
    );
  }

  return (
    <div className="text-black min-h-screen p-6" style={{
      backgroundImage: `url('${BACKGROUND_IMAGE}')`, backgroundRepeat: 'repeat', backgroundSize: 'auto'
    }}>
      <div className="flex justify-between items-center mb-4">
        <h2 className="text-lg font-semibold">Welcome, {user}</h2>
        <button onClick={handleLogout} className="bg-red-500 text-white px-2 py-1 rounded">Logout</button>
      </div>

      <div className="flex items-center gap-2 mb-4">
        <button onClick={() => changeDate(-1)}>&larr;</button>
        <input type="date" value={date} onChange={e => setDate(e.target.value)} className="p-1 border rounded" />
        <button onClick={() => changeDate(1)}>&rarr;</button>
        <select value={exportRange} onChange={e => setExportRange(e.target.value)} className="ml-4 p-1 border">
          <option value="week">Week</option>
          <option value="month">Month</option>
          <option value="year">Year</option>
        </select>
        <button onClick={exportToExcel} className="bg-yellow-500 px-2 py-1 rounded text-white ml-2">Export</button>
      </div>

      <div className="flex items-center gap-4 mb-4">
        <button onClick={() => setTasks(prev => ({
          ...prev, [date]: { ...current, count: (current.count || 0) + 1 }
        }))} className="bg-green-500 text-white px-3 py-1 rounded-full">+</button>
        <span className="text-lg font-semibold">{current.count || 0}</span>
        <button onClick={() => setTasks(prev => ({
          ...prev, [date]: { ...current, count: Math.max((current.count || 0) - 1, 0) }
        }))} className="bg-red-500 text-white px-3 py-1 rounded-full">-</button>
      </div>

      <input value={comment} onChange={e => setComment(e.target.value)}
             onKeyDown={e => e.key === "Enter" && addComment()}
             placeholder="Add comment and press Enter" className="p-2 border w-full mb-4 rounded" />

      <div className="space-y-2">
        {(current.comments || []).map((c, i) => (
          <div key={i}
               className="bg-white text-black p-3 rounded shadow flex flex-col sm:flex-row sm:items-start sm:justify-between"
               style={{ wordWrap: 'break-word', overflowWrap: 'break-word', width: '100%' }}>
            <div className="flex-1 break-words w-full">
              <p className="whitespace-pre-wrap break-words w-full overflow-hidden break-all" style={{ hyphens: 'auto' }}>{c.text}</p>
              <p className="text-xs text-gray-500 mt-1 flex items-center gap-1">
                <img src={CLOCK_ICON_URL} alt="Clock" className="w-4 h-4 inline-block" /> {c.time}
              </p>
            </div>
            <div className="flex flex-wrap gap-2 mt-2 sm:mt-0 sm:ml-4 justify-end">
              <button className="text-blue-500 hover:underline text-sm">Edit</button>
              <button className="text-red-500 hover:underline text-sm">Delete</button>
              <button className="text-green-500 hover:underline text-sm" onClick={() => navigator.clipboard.writeText(c.text)}>Copy</button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default App;
