import React, { useState, useEffect } from "react";
import * as XLSX from "xlsx";

const getCurrentDate = () => new Date().toISOString().split("T")[0];
const getCurrentTime = () => new Date().toLocaleTimeString([], { hour: '2-digit', minute: '2-digit', hour12: true });

const App = () => {
  const [loggedIn, setLoggedIn] = useState(false);
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [user, setUser] = useState("");
  const [users, setUsers] = useState(() => {
    const saved = localStorage.getItem("users");
    return saved ? JSON.parse(saved) : {};
  });
  const [tasks, setTasks] = useState({});
  const [comment, setComment] = useState("");
  const [date, setDate] = useState(getCurrentDate());
  const [exportRange, setExportRange] = useState("week");
  const [darkMode, setDarkMode] = useState(false);

  useEffect(() => {
    if (user) {
      const userTasks = localStorage.getItem(`tasks_${user}`);
      setTasks(userTasks ? JSON.parse(userTasks) : {});
    }
  }, [user]);

  useEffect(() => {
    if (user) {
      localStorage.setItem(`tasks_${user}`, JSON.stringify(tasks));
    }
  }, [tasks, user]);

  useEffect(() => {
    localStorage.setItem("users", JSON.stringify(users));
  }, [users]);

  const handleLogin = () => {
    if (users[username] && users[username] === password) {
      setLoggedIn(true);
      setUser(username);
    } else {
      alert("Invalid username or password!");
    }
  };

  const handleRegister = () => {
    if (username in users) {
      alert("User already exists!");
    } else {
      setUsers((prev) => ({ ...prev, [username]: password }));
      alert("User registered successfully! Now you can log in.");
    }
  };

  const handleCount = (delta) => {
    setTasks((prev) => {
      const current = prev[date] || { count: 0, comments: [] };
      return {
        ...prev,
        [date]: { ...current, count: Math.max(0, current.count + delta) },
      };
    });
  };

  const handleComment = () => {
    if (!comment.trim()) return;
    const newComment = {
      text: comment.trim(),
      time: getCurrentTime(),
    };
    setTasks((prev) => {
      const current = prev[date] || { count: 0, comments: [] };
      const comments = Array.isArray(current.comments) ? current.comments : [];
      return {
        ...prev,
        [date]: {
          ...current,
          comments: [...comments, newComment],
        },
      };
    });
    setComment("");
  };

  const handleCommentKeyPress = (e) => {
    if (e.key === "Enter") {
      e.preventDefault();
      handleComment();
    }
  };

  const handleEditComment = (index) => {
    const edited = prompt("Edit your comment:", current.comments[index].text);
    if (edited !== null) {
      const updated = [...current.comments];
      updated[index].text = edited;
      setTasks((prev) => ({ ...prev, [date]: { ...prev[date], comments: updated } }));
    }
  };

  const handleDeleteComment = (index) => {
    if (window.confirm("Are you sure you want to delete this comment?")) {
      const updated = [...current.comments];
      updated.splice(index, 1);
      setTasks((prev) => ({ ...prev, [date]: { ...prev[date], comments: updated } }));
    }
  };

  const handleCopyComment = (text) => {
    navigator.clipboard.writeText(text);
    alert("Comment copied to clipboard!");
  };

  const exportExcel = () => {
    const rows = [["Date", "Count", "Comments"]];
    for (const [key, val] of Object.entries(tasks)) {
      const d = new Date(key);
      const now = new Date(date);
      if (
        (exportRange === "week" && getWeek(d) === getWeek(now)) ||
        (exportRange === "month" && d.getMonth() === now.getMonth()) ||
        (exportRange === "year" && d.getFullYear() === now.getFullYear())
      ) {
        const commentsArray = Array.isArray(val.comments) ? val.comments.map(c => `${c.text} (${c.time})`) : [];
        rows.push([key, val.count, commentsArray.join("; ")]);
      }
    }
    const wb = XLSX.utils.book_new();
    const ws = XLSX.utils.aoa_to_sheet(rows);
    XLSX.utils.book_append_sheet(wb, ws, "Tasks");
    XLSX.writeFile(wb, `tasks-${exportRange}-${user}.xlsx`);
  };

  const getWeek = (date) => {
    const onejan = new Date(date.getFullYear(), 0, 1);
    return Math.ceil((((date - onejan) / 86400000) + onejan.getDay() + 1) / 7);
  };

  const rawCurrent = tasks[date] || { count: 0, comments: [] };
  const current = {
    ...rawCurrent,
    comments: Array.isArray(rawCurrent.comments) ? rawCurrent.comments : [],
  };

  return (
    <div className={`${darkMode ? 'bg-gray-900 text-white' : 'bg-[url(https://www.transparenttextures.com/patterns/cubes.png)] bg-white'} min-h-screen p-6`}>
      <div className={`${darkMode ? 'bg-gray-800 text-white' : 'bg-white'} rounded-2xl shadow-xl p-8 max-w-2xl mx-auto animate-fadeIn`}>
        <div className="flex justify-between items-center mb-6">
          <div className="flex items-center gap-3">
            <img src="https://img.icons8.com/clouds/48/task.png" alt="logo" />
            <h1 className="text-2xl font-bold">{user}'s Task Manager</h1>
          </div>
          <div className="flex gap-2">
            <button onClick={() => setDarkMode(!darkMode)} className="text-sm bg-purple-500 hover:bg-purple-600 text-white px-3 py-1 rounded">{darkMode ? '☀️ Light' : '🌙 Dark'}</button>
            <button onClick={() => window.location.reload()} className="text-sm bg-gray-300 hover:bg-gray-400 px-3 py-1 rounded">Logout</button>
          </div>
        </div>
        <input
          type="date"
          value={date}
          onChange={(e) => setDate(e.target.value)}
          className="border p-2 rounded w-full mb-4 focus:ring-2 focus:ring-green-300 text-black"
        />
        <div className="flex justify-between items-center mb-4">
          <span className="text-lg font-semibold">🎯 Tickets: {current.count}</span>
          <div className="flex gap-2">
            <button onClick={() => handleCount(1)} className="bg-green-500 hover:bg-green-600 text-white px-3 py-1 rounded-full shadow">+</button>
            <button onClick={() => handleCount(-1)} className="bg-red-500 hover:bg-red-600 text-white px-3 py-1 rounded-full shadow">-</button>
          </div>
        </div>
        <div className="mb-4 flex items-center gap-2">
          <input
            type="text"
            value={comment}
            placeholder="💬 Add comment"
            onChange={(e) => setComment(e.target.value)}
            onKeyDown={handleCommentKeyPress}
            className="border p-2 rounded w-full focus:ring-2 focus:ring-green-300 text-black"
          />
          <button onClick={handleComment} className="bg-blue-500 hover:bg-blue-600 text-white px-3 py-2 rounded-full shadow">💾</button>
        </div>
        {current.comments.length > 0 && (
          <div className="mt-4 p-4 rounded-lg shadow-inner bg-opacity-80">
            <h2 className="font-semibold mb-2">💬 Saved Comments</h2>
            <ul className="space-y-2">
              {current.comments.map((c, i) => (
                <li key={i} className="break-words whitespace-pre-wrap p-2 rounded shadow flex justify-between items-start bg-white text-black dark:bg-gray-700 dark:text-white">
                  <div>
                    <p>{c.text}</p>
                    <span className="text-xs text-gray-500">⏰ {c.time}</span>
                  </div>
                  <div className="flex flex-col gap-1 ml-2">
                    <button onClick={() => handleEditComment(i)} className="text-blue-500 hover:underline text-sm">Edit</button>
                    <button onClick={() => handleDeleteComment(i)} className="text-red-500 hover:underline text-sm">Delete</button>
                    <button onClick={() => handleCopyComment(c.text)} className="text-green-500 hover:underline text-sm">Copy</button>
                  </div>
                </li>
              ))}
            </ul>
          </div>
        )}
        <div className="mt-6 flex items-center gap-3">
          <select value={exportRange} onChange={(e) => setExportRange(e.target.value)} className="border p-2 rounded text-black">
            <option value="week">This Week</option>
            <option value="month">This Month</option>
            <option value="year">This Year</option>
          </select>
          <button onClick={exportExcel} className="bg-yellow-500 hover:bg-yellow-600 text-white py-2 px-4 rounded shadow">📤 Export</button>
        </div>
      </div>
    </div>
  );
};

export default App;
